import streamlit as st

# Sample product data
products = [
    {"id": 1, "name": "üíÑLipstick", "price": 25, "image": "data:image/jpeg;base64,"},
    {"id": 2, "name": "üß¥Foundation", "price": 40, "image": "data:image/jpeg;base64,"},
    {"id": 3, "name": "‚úèÔ∏èEyeliner", "price": 15, "image": "data:image/png;base64,"},
    {"id": 4, "name": "üé≠Mascara", "price": 20, "image": "data:image/jpeg;base64,"},
    {"id": 5, "name": "üå∏Blush", "price": 30, "image": "data:image/jpeg;base64,"},
    {"id": 6, "name": "‚ú®Highlighter", "price": 35, "image": "data:image/jpeg;base64,"},
    {"id": 7, "name": "üé®Concealer", "price": 28, "image": "data:image/jpeg;base64,"},
    {"id": 8, "name": "‚òÄÔ∏èBronzer", "price": 32, "image": "data:image/jpeg;base64,"},
    {"id": 9, "name": "üí¶Setting Spray", "price": 22, "image": "data:image/jpeg;base64,"},
    {"id": 10, "name": "üõ¢Ô∏èPrimer", "price": 27, "image": "data:image/jpeg;base64,"},
]
import streamlit as st

# Initialize session state for cart, checkout, order confirmation, and wishlist
if "cart" not in st.session_state:
    st.session_state.cart = []
if "checkout" not in st.session_state:
    st.session_state.checkout = False
if "order_confirmed" not in st.session_state:
    st.session_state.order_confirmed = False
if "customer_details" not in st.session_state:
    st.session_state.customer_details = {}
if "wishlist" not in st.session_state:
    st.session_state.wishlist = []

st.title("üõçÔ∏èüéÅ E-Commerce App - Shop With Joy! üéà‚ú®")

# Display products only if checkout is not active
if not st.session_state.checkout and not st.session_state.order_confirmed:
    def display_products():
        cols = st.columns(2)
        for i, product in enumerate(products):
            with cols[i % 2]:
                st.image(product["image"], width=150)
                st.write(f"**{product['name']}** üõí")
                st.write(f"üí≤ {product['price']} üè∑Ô∏è")
                if st.button(f"üõçÔ∏è Add to Cart {product['id']}", key=f"cart_{product['id']}"):
                    st.session_state.cart.append(product)
                    st.success(f"‚úÖ {product['name']} added to cart! üõíüéâ")
                if st.button(f"‚ù§Ô∏è Add to Wishlist {product['id']}", key=f"wishlist_{product['id']}"):
                    st.session_state.wishlist.append(product)
                    st.info(f"üåü {product['name']} added to wishlist! üíñ")

    display_products()

# Sidebar Cart Section
st.sidebar.header("üõí Your Shopping Cart")
if st.session_state.cart:
    total_price = sum(item["price"] for item in st.session_state.cart)

    for item in st.session_state.cart:
        st.sidebar.write(f"üõçÔ∏è {item['name']} - üí≤{item['price']}")

    st.sidebar.write(f"**Total: üí∞üí≤{total_price}**")

    if st.sidebar.button("üóëÔ∏è Clear Cart"):
        st.session_state.cart = []
        st.session_state.checkout = False
        st.session_state.order_confirmed = False
        st.sidebar.success("üõí Cart cleared! üöÄ")

    if st.sidebar.button("üí≥ Proceed to Checkout"):
        st.session_state.checkout = True
        st.rerun()
else:
    st.sidebar.write("üõçÔ∏è Your cart is empty! üõí")

# Sidebar Wishlist Section
st.sidebar.header("üíñ Your Wishlist")
if st.session_state.wishlist:
    for item in st.session_state.wishlist:
        st.sidebar.write(f"üåü {item['name']} - üí≤{item['price']}")
    
    if st.sidebar.button("üóëÔ∏è Clear Wishlist"):
        st.session_state.wishlist = []
        st.sidebar.success("üíñ Wishlist cleared! ‚ú®")
else:
    st.sidebar.write("üíñ Your wishlist is empty!")

# Checkout and Billing Section
if st.session_state.checkout and not st.session_state.order_confirmed:
    st.header("üßæ Checkout & Billing üì¶")
    st.subheader("üìã Order Summary")

    total_price = sum(item["price"] for item in st.session_state.cart)

    for item in st.session_state.cart:
        st.write(f"‚úÖ {item['name']} - üí≤{item['price']}")

    st.write(f"**Total Amount: üí∞üí≤{total_price}**")

    # Billing Form
    st.subheader("üìù Billing Details")
    name = st.text_input("üë§ Full Name")
    email = st.text_input("üìß Email Address")
    address = st.text_area("üè† Shipping Address")

    col1, col2 = st.columns(2)

    with col1:
        if st.button("‚úÖ Confirm Purchase"):
            if name and email and address:
                st.session_state.customer_details = {
                    "name": name,
                    "email": email,
                    "address": address,
                    "total_price": total_price,
                    "items": st.session_state.cart,
                }
                st.session_state.order_confirmed = True
                st.session_state.cart = []  # Clear cart after purchase
                st.session_state.checkout = False
                st.rerun()
            else:
                st.error("‚ö†Ô∏è Please fill in all billing details!")

    with col2:
        if st.button("üîô Back to Shopping"):
            st.session_state.checkout = False
            st.rerun()

# Success Page with Bill Receipt and Download Button
if st.session_state.order_confirmed:
    st.header("üéâ‚úÖ Purchase Successful! üéä")
    st.success(f"üéà Thank you, {st.session_state.customer_details['name']}! Your order has been placed. üõçÔ∏èüéÅ")

    st.subheader("üßæ Order Receipt üìú")
    st.write(f"**üë§ Customer Name:** {st.session_state.customer_details['name']}")
    st.write(f"**üìß Email:** {st.session_state.customer_details['email']}")
    st.write(f"**üè† Shipping Address:** {st.session_state.customer_details['address']}")

    st.write("üõçÔ∏è **Order Details:**")
    for item in st.session_state.customer_details["items"]:
        st.write(f"‚úÖ {item['name']} - üí≤{item['price']}")

    st.write(f"üí∞ **Total Paid: üí≤{st.session_state.customer_details['total_price']}**")

    # Create Receipt Content
    receipt_content = f"""
    üéÅüõçÔ∏è E-Commerce Receipt üõçÔ∏èüéÅ
    =================================
    üë§ Customer Name: {st.session_state.customer_details['name']}
    üìß Email: {st.session_state.customer_details['email']}
    üè† Shipping Address: {st.session_state.customer_details['address']}
    
    üõí Order Details:
    """
    for item in st.session_state.customer_details["items"]:
        receipt_content += f"‚úÖ {item['name']} - üí≤{item['price']}\n"

    receipt_content += f"\nüí∞ Total Paid: üí≤{st.session_state.customer_details['total_price']}\n"
    receipt_content += "=================================\nüéà Thank you for shopping with us! üéâüõçÔ∏è"

    # Download Receipt Button
    st.download_button(
        label="üìù Download Receipt üì•",
        data=receipt_content,
        file_name="receipt.txt",
        mime="text/plain"
    )

    # Back to Home Button
    if st.button("üè† Back to Home"):
        st.session_state.order_confirmed = False
        st.session_state.customer_details = {}
        st.rerun()
